-- Listage des données de la table ocprojet5.posts : ~4 rows (environ)
INSERT INTO `posts` (`id`, `id_user`, `title`, `summary`, `content`, `creation_date`, `last_update_date`) VALUES
	(27, 2, 'Un Site Wordpress : Chalets Et Caviar', 'Projet dont le but &amp;eacute;tait d&amp;rsquo;appr&amp;eacute;hender WordPress, ses possibilit&amp;eacute;s et diff&amp;eacute;rentes extensions.', 'Dans ce projet, r&amp;eacute;alis&amp;eacute; dans le cadre de ma formation Openclassrooms, j&amp;rsquo;ai d&amp;eacute;couvert WordPress et une partie des ses possibilit&amp;eacute;s.\r\nJ&amp;rsquo;ai utilis&amp;eacute; un th&amp;egrave;me, Ocean Extra, que j&amp;rsquo;ai pu adapter &amp;agrave; mes besoins. Pour cela j&amp;rsquo;ai choisi d&amp;rsquo;utiliser le plugin Elementor, qui facilite grandement la mise en page.\r\nJ&amp;rsquo;ai &amp;eacute;galement pu tester l&amp;rsquo;utilisation d&amp;rsquo;autres plugins, comme par exemple :\r\nAskimet, une protection anti spam efficace,\r\nUser Role Editor afin de g&amp;eacute;rer les autorisations de diff&amp;eacute;rents utilisateurs,\r\nWPforms Lite, pour g&amp;eacute;rer le formulaire de contact,\r\nCentral color palette, afin d&amp;rsquo;acc&amp;eacute;der facilement &amp;agrave; ma charte graphique lors de la cr&amp;eacute;ation des diff&amp;eacute;rentes pages,\r\nYoast SEO, afin de g&amp;eacute;rer le r&amp;eacute;f&amp;eacute;rencement du site,\r\net enfin Estatik, un plug in de gestion de biens immobiliers, th&amp;egrave;me de l&amp;rsquo;exercice .\r\nCe site est visible &amp;agrave; l&amp;#039;adresse : https://chaletsetcaviar.marinesanson.fr', '2023-10-25 15:25:38', '2023-10-25 15:29:12'),
	(28, 2, 'Site Accou&amp;rsquo;ferme', 'Projet de fin de formation Bac +2, r&amp;eacute;alis&amp;eacute; avec Php et Javascript.', 'Projet de fin de formation Bac+2 &amp;quot;D&amp;eacute;veloppeuse Web et Web mobile, j&amp;#039;ai r&amp;eacute;alis&amp;eacute; ce site d&amp;#039;apr&amp;egrave;s la demande d&amp;#039;une personne qui a pour projet de s&amp;#039;installer comme mara&amp;icirc;cher Bio. Le site reste pour l&amp;#039;instant en l&amp;#039;&amp;eacute;tat mais il a vocation &amp;agrave; &amp;ecirc;tre mis en ligne dans quelque temps.\r\nLa demande &amp;eacute;tait d&amp;rsquo;utiliser tout ce que nous avions vu en formation :\r\n HTML5,\r\n CSS3,\r\n Javascript,\r\n Php 7.4 (sans framework),\r\n MySQL.\r\nEn r&amp;eacute;alisant ce site, j&amp;rsquo;ai appris &amp;agrave; manipuler des objets en Php, faire des requ&amp;ecirc;tes SQL avec des Join et des conditions.\r\nLe point fort de ce site, en tout cas ce qui m&amp;rsquo;a demand&amp;eacute; le plus de temps et d&amp;rsquo;efforts, est la r&amp;eacute;alisation d&amp;rsquo;un panier de commande en Js, avec une fonctionnalit&amp;eacute; + et &amp;ndash; qui met &amp;agrave; jour la quantit&amp;eacute; du panier en Js et la transmet &amp;agrave; Php via un fichier .json.\r\nCe site attend, bien au chaud, que la personne qui l&amp;rsquo;a demand&amp;eacute; s&amp;rsquo;installe &amp;agrave; son compte, et il devrait &amp;ecirc;tre mis en ligne quand cette personne s&amp;rsquo;installera (non sans quelques modifications car l&amp;rsquo;avanc&amp;eacute;e dans mes &amp;eacute;tudes me fait voir les choses d&amp;rsquo;une autre mani&amp;egrave;re et je me rends compte aujourd&amp;rsquo;hui qu&amp;rsquo;il y a pas mal de points qui peuvent &amp;ecirc;tre optimis&amp;eacute;s).', '2023-10-27 07:50:31', '2023-10-27 07:50:31'),
	(29, 2, 'Luttes Enchant&amp;eacute;es : Besoin De D&amp;eacute;part', 'Apr&amp;egrave;s ma 1&amp;egrave;re formation, j&amp;rsquo;ai r&amp;eacute;alis&amp;eacute; un site pour une chorale, afin d&amp;rsquo;avoir un outil plus intuitif qu&amp;rsquo;une dropbox pour acc&amp;eacute;der aux diff&amp;eacute;rentes voix.', 'Membre d&amp;rsquo;une chorale qui utilisait une dropbox pour acc&amp;eacute;der &amp;agrave; des fichiers sons des diff&amp;eacute;rentes voix pour pouvoir r&amp;eacute;p&amp;eacute;ter &amp;agrave; la maison, je me suis aper&amp;ccedil;ue que cet outil &amp;eacute;tait difficile d&amp;rsquo;acc&amp;egrave;s pour les personnes d&amp;rsquo;un certain &amp;acirc;ge. Afin de faciliter les choses pour ces personnes, et pour mettre en pratique mes connaissances entre mes deux formations, j&amp;rsquo;ai cr&amp;eacute;&amp;eacute; un site internet pour cette chorale.\r\nPour ce site j&amp;rsquo;ai utilis&amp;eacute; les langages vus en formation : HTML5, CSS3, Php 7.4 (sans framework),et MySQL.\r\nUne page affiche tous les chants de la chorale, class&amp;eacute;s par cat&amp;eacute;gories, et quand on clique dessus on acc&amp;egrave;de &amp;agrave; une page avec le d&amp;eacute;tail d&amp;rsquo;un chant, avec une description, le pdf des paroles et les diff&amp;eacute;rents fichiers sons (voie aigue, medium et grave, et parfois un enregistrement de la chorale). Il y a, pour certains, une vid&amp;eacute;o (lien youtube) de la version originale. Le pdf et les fichiers sons sont t&amp;eacute;l&amp;eacute;chargeables.\r\nJ&amp;rsquo;ai cod&amp;eacute; &amp;eacute;galement un espace d&amp;rsquo;administration, avec un upload des fichiers, et la possibilit&amp;eacute; d&amp;rsquo;afficher les vid&amp;eacute;os sur la page d&amp;rsquo;accueil (publique), pour les chants du moment.\r\nLe pari est r&amp;eacute;ussi car j&amp;#039;ai de bons retour de ce site et les personnes qui n&amp;#039;&amp;eacute;taient pas &amp;agrave; l&amp;#039;aise avec la dropbox utilisent beaucoup plus le site !', '2023-10-27 09:07:18', '2023-10-27 09:07:18'),
	(30, 2, 'Luttes Enchant&amp;eacute;es : Le Bonus', 'Afin de faire du lien entre les membres, le site dispose d&amp;rsquo;une messagerie et d&amp;rsquo;un espace d&amp;rsquo;&amp;eacute;change.', 'Le but de cette chorale, en plus de chanter ensemble, est de faire du lien entre les membres. Pour cela, lorsque j&amp;rsquo;ai cr&amp;eacute;&amp;eacute; le site internet (pour faciliter l&amp;rsquo;acc&amp;egrave;s aux fichiers sons comme je l&amp;rsquo;explique dans un autre article : Luttes Enchant&amp;eacute;es : besoin de d&amp;eacute;part), j&amp;rsquo;ai cr&amp;eacute;&amp;eacute; deux espaces : un espace de messagerie et un espace d&amp;rsquo;&amp;eacute;changes.\r\nL&amp;rsquo;espace de messagerie sert &amp;agrave; &amp;eacute;changer ensemble sans utiliser les mails. Cela permet de discuter et s&amp;rsquo;organiser. Il est utilis&amp;eacute; par exemple afin de covoiturer pour se rendre aux concerts, se donner rendez-vous pour aller au cin&amp;eacute;&amp;hellip;\r\nL&amp;rsquo;espace d&amp;rsquo;&amp;eacute;change, quant &amp;agrave; lui, sert plus &amp;agrave; se conseiller des spectacles, des livres, des films, se partager des recettes de cuisines&amp;hellip;\r\nLe site est r&amp;eacute;cent mais les membres se le sont appropri&amp;eacute; et l&amp;rsquo;utilisent.', '2023-10-27 09:21:41', '2023-10-27 09:21:41');

